import React, {useState} from "react";
import logo from '../source/images/svg-images/mesto_logo.svg';
import {Link, useHistory} from "react-router-dom";
 function Header({isLoggedIn}){
     const history = useHistory();
     console.log(history.location.pathname)
     const [location, setLocation] = useState(history.location.pathname)
     const updateLocation = () => {
         setLocation(history.location.pathname);
     }
    const getMarkUp = () => {
        if (isLoggedIn){
            return (<p className="header__menu">dodik@gmail.com {<Link className="header__menu-link" to="/sign-in">Выйти</Link>}</p>)
        }else{
            return  history.location.pathname === '/sign-up' ? (<Link onClick={updateLocation} className="header__menu-link" to="../sign-in">Войти</Link>) : (<Link onClick={updateLocation} className="header__menu-link" to="../sign-up">Регистрация</Link>)
        }
    }
    return(
        <header className="header page__header">
            <img className="header__logo" src={logo} alt="Лого"/>
            {
                getMarkUp()
            }
            {/*{history.location.pathname === '/sign-up' && !isLoggedIn ? <Link to="../sign-in">Войти</Link> : <Link to="../sign-up">Войти</Link>}*/}

        </header>
    );
}

export default Header;
