import {Link, useHistory, useRouteMatch} from "react-router-dom";
 function Header({isLoggedIn}) {
    const {path, url} = useRouteMatch();

     const getMarkUp = () => {
         if (path === '/'){
             return (<p className="header__menu">dodik@gmail.com {<Link className="header__menu-link" to="/sign-in">Выйти</Link>}</p>)
         }else{
             return  path === '/sign-up' ? (<Link o className="header__menu-link" to="../sign-in">Войти</Link>) : (<Link  className="header__menu-link" to="../sign-up">Регистрация</Link>)
         }
      }
    return(
        <header className="header page__header">
            <img className="header__logo" src={logo} alt="Лого"/>
            {getMarkUp()}
        </header>
    );
}

export default Header;
