import React from "react";
import logo from '../source/images/svg-images/mesto_logo.svg';
import {Link, useHistory} from "react-router-dom";
 function Header({isLoggedIn}){
     const history = useHistory();
     console.log(history.location.pathname)

    return(
        <header className="header page__header">
            <img className="header__logo" src={logo} alt="Лого"/>
            {
                () => {
                    if (isLoggedIn){
                       return (<p className="header__menu">dodik@gmail.com {<Link to="/sign-in">Выйти</Link>}</p>)
                    }else{
                       return  history.location.pathname === '/sign-up' ? (<Link to="../sign-in">Войти</Link>) : (<Link to="../sign-up">Регистрация</Link>)
                    }
                }
            }
            {/*{history.location.pathname === '/sign-up' && !isLoggedIn ? <Link to="../sign-in">Войти</Link> : <Link to="../sign-up">Войти</Link>}*/}

        </header>
    );
}

export default Header;
