        const {name, value} = evt.target;
        setFormState({[name]: value});
    }
    return(
        <main className="content">
            <section className="register page__register">
                <h2 className="register__title">Регистрация</h2>
                <form className="register__form">
                    <Input value={""}
                           id={""}
                           maxLength={""}
