                           onChangeHandler={handleChange}
                           type={""}
                           styleModifier="register__input"
                           placeholder="Email"
                    />
                    <Input value={""}
                           id={""}
                           maxLength={""}
                           minLength={""}
                           onChangeHandler={handleChange}
                           type={""}
