                           placeholder="Пароль"
                    />
                    <SignButton value="Зарегестрироваться" onSubmit={''}/>
                </form>
                    <p className="register__sign-in-caption">Уже зарегистрированы? {<Link to="../sign-in" className="register__sign-in-link">Войти</Link>}</p>
            </section>
        </main>
    );
}
